sp_widget.client_script:
function($scope, $http, $rootScope, $timeout, spUtil) {
  var c = this;

	$rootScope.$on('showSecActuals', function() {
   $timeout(function(){
	  $http.get('/api/x_saic4_saic_team/teammgmtutils/getActualsStats')
		.then(function(res) {
			c.data.budRec = res.data.result;
      _createChart();
	  });
	 });
  });

	function _createChart() {
     c.myChart = new Highcharts.Chart({
			  chart: {
						 renderTo: 'myPBCGBar',
					   defaultSeriesType: 'line'
        },
				title: {
						text: 'Actuals by project over SAIC Periods'
				},

				subtitle: {
						text: ''
				},

				yAxis: {
						title: {
								text: 'Training Investment Actuals'
						}
				},
				legend: {
						layout: 'vertical',
						align: 'right',
						verticalAlign: 'middle'
				},

				plotOptions: {
						series: {
								label: {
										connectorAllowed: false
								},
								pointStart: 1
						}
				},
        series: c.data.budRec,

    responsive: {
        rules: [{
            condition: {
                maxWidth: 500
            },
            chartOptions: {
                legend: {
                    layout: 'horizontal',
                    align: 'center',
                    verticalAlign: 'bottom'
                }
            }
        }]
    }


		});

	}

}
