sp_widget.client_script:
function ($http, $scope, $timeout, $animate, $rootScope, $location, $window) {
	var c = this;

  // *************************************************************
	// Start tab Control
	$scope.active = 1;
	$scope.selectTab = function(value){
		$scope.active = value;
	};

	$scope.isActive = function(value){
		if($scope.active==value){
			return true;
		}
		else{
			return false;
		}
	};
	// End Tab Control
  // *************************************************************

	$rootScope.$on('loadChart', function(event,data) {	
     c.showWidget = true;
//		 alert(data);
		 c.chartArea = data
     $http.get('/api/x_saic4_saic_team/teammgmtutils/getPieStats?sysUserID='+$location.search().sys_id+'&area='+c.chartArea)
       .then(function(res) {
          $scope.data.title  = res.data.result[0].title;
          $scope.data.cats   = res.data.result[0].categories;
          $scope.data.counts = res.data.result[0].data;
          buildChart();
       });
  });
  $rootScope.$on('loadChartOff', function(event,data) {	
		 c.showWidget = false;
  });

	function buildChart() {
		    //fLen = $scope.data.chartdata.length;
		    //var st = "'something',32";
		    //var chartText =[];
		    //chartText.push(st);
		    //for (i = 0; i < fLen; i++) {
        //    chartText[i] = [$scope.data.chartdata[i]];
        //}
		    //var something = [];
        //chartText[0]= ['something',32];
		    //chartText = chartText.substr(0,chartText.length-1);
		   // alert(isArray(chartText));
	     //	chartText = $scope.data.chartdata;
		   //alert(chartText);

	      Highcharts.setOptions({
           colors: 
             ['#cccccc','#85a545','#006BB6'] 
			  });
			  Highcharts.chart('container', {
         chart: {
					type: 'bar'
					},
					title: {
						text: $scope.data.title
					},
          credits: {
            enabled: false
          },
          subtitle: {
            text: 'Source: <a href="https://openair.com">RMS</a>'
          },
          xAxis: {
						categories: $scope.data.cats,
            type: 'category',
            labels: {
                //rotation: -90,
                style: {
                    fontSize: '10px',
                    fontFamily: 'Verdana, sans-serif'
                }
            }
          },
          yAxis: {
            min: 0,
            title: {
                text: 'Counts'
            }
          },
          legend: {
            enabled: false
          },
          tooltip: {
             pointFormat: 'Count from RMS: <b>{point.y} </b>'
          },
					plotOptions: {
						column: {
							//stacking: 'normal',
							dataLabels: {
								enabled: true,
								formatter: function () {
									return "$" +this.point.y.toFixed(2).replace(/\B(?=(\d{3})+(?!\d))/g, ",");
								}
							}

						}
					},
          series: [{
            name: 'Agencies',
            data: $scope.data.counts,
                  dataLabels: {
                    enabled: true,
                    //rotation: -90,
                    color: '#FFFFFF',
                    align: 'right',
                    format: '{point.y}', // one decimal
                    y: 10, // 10 pixels down from the top
                    style: {
                       fontSize: '13px',
                       fontFamily: 'Verdana, sans-serif'
                    }
                  }
                }]
          });
	}
}
