sp_widget.client_script:
function ($http, $scope, $timeout, $animate, $rootScope, $location, $window) {
	var c = this;

  // *************************************************************
	// Start tab Control
	$scope.active = 1;
	$scope.selectTab = function(value){
		$scope.active = value;
	};

	$scope.isActive = function(value){
		if($scope.active==value){
			return true;
		}
		else{
			return false;
		}
	};
	// End Tab Control
  // *************************************************************

  c.action = function(state) {
//	  alert()
    $http.get('/api/x_saic4_saic_team/teammgmtutils/updateCatRequest?sysapprover='+$location.search().sysapprover+'&reqState='+state+'&comments=rejected from portal')
     .then(function(sysapprovalres) {
        $scope.data.rslts = sysapprovalres.data.result;
		alert($scope.data.rslts);
		$window.location.href = '/team';
    });
  };
  $http.get('/api/x_saic4_saic_team/teammgmtutils/getRequestApprovalStatus?reqSysID='+$location.search().reqSysID)
    .then(function(resStatus) {
     $scope.data.stats = resStatus.data.result;
  });

	$http.get('/api/x_saic4_saic_team/teammgmtutils/getTrainRequest?reqID='+$location.search().reqSysID)
    .then(function(res) {
     $scope.data.trnReq = res.data.result;
        var userID   = $scope.data.trnReq.requested_for;
        var reqSysID = $scope.data.trnReq.sys_id;
        var fy       = $scope.data.trnReq.fy;
		    if (userID) {
           getHistory(userID, reqSysID, fy);
				}
  });

	function getHistory(userID, reqSysID, fy){
//		alert("userID: "+ userID+ ", reqSysID: "+ reqSysID + ", fy: " +fy);
		$timeout(function(){	
      $http.get('/api/x_saic4_saic_team/teammgmtutils/getAllUserTrnReqs?usrSysID='+userID+'&reqSysID='+reqSysID+'&fy='+fy)
	       .then(function(res2) {
         $scope.data.allTrnReq = res2.data.result;
		  });
    });
    buildChart(userID,fy);
	}

	function buildChart(userID,fy) {
		$timeout(function(){	
           $http.get('/api/x_saic4_saic_team/teammgmtutils/getReqTotImct?usrSysID='+userID+'&fy='+fy)
	       .then(function(res3) {
              $scope.data.chartData = res3.data.result;
		      Highcharts.setOptions({
                colors: 
				   ['#cccccc','#85a545','#006BB6'] 
			  });
			  Highcharts.chart('container', {
         chart: {
					type: 'column'
					},
					title: {
						text: 'Demand vs Actual'
					},
					subtitle: {
						text: ''
					},
					xAxis: {
						categories: ['Demand', 'Actuals']
					},
					yAxis: {
						min: 0,
						title: {
							text: 'Total Demand ($)'
					    },
		                stackLabels: {
                            enabled: true,
                            style: {
                               fontWeight: 'bold',
                               color: (Highcharts.theme && Highcharts.theme.textColor) || 'gray'
                            }
				        }
					},
					tooltip: {
						headerFormat: '<span style="font-size:10px">{point.key}</span><table>',
						pointFormat: '<tr><td style="color:{series.color};padding:0">{series.name}: </td>' +
						'<td style="padding:0"><b>$ {point.y:.1f}</b></td></tr>',
						footerFormat: '</table>',
						shared: true,
						useHTML: true
					},
					plotOptions: {
						column: {
							stacking: 'normal',
							dataLabels: {
								enabled: true,
								formatter: function () {
									return "$" +this.point.y.toFixed(2).replace(/\B(?=(\d{3})+(?!\d))/g, ",");
								}
							}

						}
					},
					series: 
					[{
                       stack: 1,
                       name: 'Pending',
                       data: [$scope.data.chartData.totPending]
					}, {
                       stack: 1,
                       name: 'Approved',
                       data: [$scope.data.chartData.totApproved]
					}, {
                       stack: 2,
                       name: 'Actual',
                       data: [$scope.data.chartData.totActuals]
					}]
              });
           });
       });
	}
}

sp_widget.css:
/* tab color */
.nav-tabs>li>a {
  background-color: #333333; 
  border-color: #777777;
  color:#fff;
}

/* active tab color */
.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
  color: #fff;
  background-color: #666;
  border: 1px solid #888888;
}

/* hover tab color */
.nav-tabs>li>a:hover {
  border-color: #000000;
  background-color: #111111;
}
