sys_script_include.script:
var TeamMgmt = Class.create();
TeamMgmt.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	 // *****************************************************************************
     // Return all sys_id's of all employees where the mgr_sys_id is the manager 
     // and return all employees that may also have subordinates.
     // *****************************************************************************
     getMyTeamSysIDs: function(mgr_sys_id,usr_sys_id) {
//		mgr_sys_id = gs.getUserID();
        var in_scope_users = "sys_idIN"+mgr_sys_id+','+usr_sys_id;
        var usr = new GlideRecord('sys_user');
        usr.addQuery('manager',mgr_sys_id);
		usr.addQuery('active',true);
        usr.query();
        while (usr.next()) {
          if (in_scope_users.length > 0) {
            //build a comma separated string of groups if there is more than one
            in_scope_users+= (',' + usr.sys_id).toString();
			in_scope_users+= (',' + getExtendedTeam2(usr.sys_id)).toString();
          }
          else
		  {
            in_scope_users= usr.sys_id.toString();
			in_scope_users+= (',' + getExtendedTeam2(usr.sys_id)).toString();
          }
		}

		return in_scope_users;

        // sabados - I know there's a better way to do this recursively but I don't know how to do that so taking the brute force approach
		// ******************************************************************************************************************************
        // Retrieve any level 2 employees   e.g., if the user is moraco, this would be his direct reports (e.g., Nazzik )
        // ******************************************************************************************************************************
        function getExtendedTeam2(user_sys_id) {
		   var additional_members2 = "";
           var in_scope_users = "sys_idIN";
           var moreusr2 = new GlideRecord('sys_user');
           moreusr2.addQuery('manager',user_sys_id);
	       moreusr2.addQuery('active',true);
           moreusr2.query();
           while (moreusr2.next()) {
             if (additional_members2.length > 0) {
               //build a comma separated string of groups if there is more than one
               additional_members2+= (',' + moreusr2.sys_id).toString();
   			   additional_members2+= (',' + getExtendedTeam3(moreusr2.sys_id)).toString();
             }
             else
		     {
               additional_members2= moreusr2.sys_id.toString();
   			   additional_members2+= (',' + getExtendedTeam3(moreusr2.sys_id)).toString();
             }
   		   }
		   if (additional_members2.length>0)
  		      return additional_members2;

		} 

