	 // *****************************************************************************
     // Determine where in the organization the logged in user is
     // *****************************************************************************
     whoAmI: function(sysID) {
		// Calculate current FY
		var datetoconvert = new GlideDate();
        fycal = new global.AccountingCalendar(datetoconvert);
        var fy = fycal.fy; //shows Fiscal Year

		var dd = new TeamMgmt();
        var myData = [];
		var me  = sysID.toString();

		// 1) do I manage any of the budget owners?
	    var amiSectMgr = new GlideRecord('x_saic4_saic_team_training_budget');
        amiSectMgr.addEncodedQuery('budget.u_level_1_manager_id='+me);
		amiSectMgr.addQuery("fy",fy);
        amiSectMgr.query();
        while (amiSectMgr.next()) {
//		  if (amiSectMgr.next()) {
			var budtot    = dd.formatDollar(amiSectMgr.getValue('budget_total'));
			var budbal    = dd.formatDollar(amiSectMgr.getValue('balance'));
			var budact    = dd.formatDollar(amiSectMgr.getValue('actuals_to_date'));
			var budpen    = dd.formatDollar(amiSectMgr.getValue('pending_demand'));
			var budapp    = dd.formatDollar(amiSectMgr.getValue('approved_demand'));
			var totPen    = parseInt(amiSectMgr.pending_demand);
			var totAprvd  = parseInt(amiSectMgr.approved_demand);
			var totDemand = totPen + totAprvd;
			totDemand     = dd.formatDollar(totDemand);
            myData.push({
			  "rolename"      : "sector mgr",
              "budget"        : amiSectMgr.getDisplayValue('budget'),
              "totalBudget"   : budtot,
              "budgetBalance" : budbal,
              "totActuals"    : budact,
              "totPending"    : budpen,
              "totApproved"   : budapp,
			  "totDemand"     : totDemand
		    });
//		  } 
		}
        if (Array.isArray(myData) && myData.length > 0) {
			return myData;
		}

		// 2) do I own a budget???
		var amibudgetOwner = new GlideRecord('x_saic4_saic_team_training_budget');
		amibudgetOwner.addQuery("fy",fy);
        amibudgetOwner.addQuery('budget', me);
        amibudgetOwner.query();
        while (amibudgetOwner.next()) {
//		  if (amibudgetOwner.next()) {
            myData.push({
              "rolename"      : "budget mgr",
              "budget"        : amibudgetOwner.getDisplayValue('budget'),
              "totalBudget"   : amibudgetOwner.getValue('budget_total'),
              "budgetBalance" : amibudgetOwner.getValue('balance'),
              "totActuals"    : amibudgetOwner.getValue('actuals_to_date'),
              "totPending"    : amibudgetOwner.getValue('pending_demand'),
              "totApproved"   : amibudgetOwner.getValue('approved_demand')
		    });
//		  }
		}
        if (Array.isArray(myData) && myData.length > 0) {
		   return myData;
		}
        // 3) are there requests where I'm mgr?
        var amiSLTL = new GlideAggregate('x_saic4_saic_team_training_requests');
        amiSLTL.addAggregate('COUNT');
        amiSLTL.addQuery('level_1_manager.sys_id', me);
		amiSLTL.addQuery("fy",fy);
        amiSLTL.query();
        while (amiSLTL.next()) {
		  if (amiSLTL.getAggregate('COUNT')>0) {
			  myData.push({
				  "rolename" : "sltl",
				  "count"    : amiSLTL.getAggregate('COUNT'),
				  "itemType" : "requests"
			  });
		  }
		}
        if (Array.isArray(myData) && myData.length > 0) {
		   return myData;
		}
        myData.push({
          "rolename" : "employee",
          "count"    : "0",
          "itemType" : "employee"
        });
        if (Array.isArray(myData) && myData.length > 0) {
		   return myData;
		}

	 },

	 // *****************************************************************************
     // Return a dollar formatted number (string) of a value passed in
     // *****************************************************************************
     formatDollar: function(value) {
	    var returnval = parseInt(value);
	    returnval = returnval.toFixed(2);
	    returnval = returnval.replace(/\B(?=(\d{3})+(?!\d))/g, ",");
	    return returnval;
     },

	 // *****************************************************************************
     // Return Labor Cost Estimates
     // *****************************************************************************
     getEstimatedLabor: function(reqRequestFor,reqCourseDuration) {
		var fn = new TeamMgmt();

		var estimateGR = new GlideRecord("sys_user");
        estimateGR.addActiveQuery();
        estimateGR.addQuery("sys_id", reqRequestFor);
        estimateGR.query();
		var jobCode = "";
		while(estimateGR.next()) {
			jobCode = estimateGR.getValue('u_job_code');
		}
		var hrlyRate = fn.getJobCodeSalaryInfo(jobCode,'hour');
		var lbrEstimate = reqCourseDuration * hrlyRate;
//	    lbrEstimate = parseInt(lbrEstimate);
	    lbrEstimate = 1*lbrEstimate.toFixed(2);
//	    returnval = returnval.replace(/\B(?=(\d{3})+(?!\d))/g, ",");
	    return lbrEstimate;
     },

	 // *****************************************************************************
     // Get Job Code mid-salary
     // *****************************************************************************
     getJobCodeSalaryInfo: function(jobCode,interval) {
        var jcGR = new GlideRecord("x_saic4_saic_team_job_code_reference");
        jcGR.addQuery("job_code", jobCode);
        jcGR.query();
		var midSalary = 0;
		while(jcGR.next()) {
			midSalary = jcGR.getValue('salary_mid');
		}
		if (midSalary > 0) {
            if (interval=='year') {
               return midSalary;
            } else if (interval=='month') {
               return (midSalary/2087)*160;
			} else if (interval=='day') {
               return (midSalary/2087)*8;
			} else if (interval=='hour') {
			   return midSalary/2087;  //https://www.opm.gov/policy-data-oversight/pay-leave/pay-administration/fact-sheets/computing-hourly-rates-of-pay-using-the-2087-hour-divisor/
			}
		} else {
			return 0;
		}
     },

	 // *****************************************************************************
     // Return A persons team based on organization data
     // *****************************************************************************
     getMyTeam: function(sysUserID) {
		var fn = new TeamMgmt();
        var teamData = {};
        teamData.direct_reports = [];

        //Calculate direct reports
        var directReportGR = new GlideRecord("sys_user");
        directReportGR.addActiveQuery();
		var encQry = fn.getMyTeamSysIDs(sysUserID,sysUserID);
		directReportGR.addEncodedQuery(encQry);
//        directReportGR.addQuery("manager", sysUserID);
        directReportGR.query();
        while(directReportGR.next()) {
            teamData.direct_reports.push(
				{
                  "email"      : directReportGR.getValue("email") || "",
                  "first_name" : directReportGR.getValue("first_name"),
                  "last_name"  : directReportGR.getValue("last_name"),
                  "name"       : directReportGR.getValue("name"),
                  "phone"      : directReportGR.getValue("phone") || "",
                  "sys_id"     : directReportGR.getValue("sys_id"),
			      "pmInfo"     : fn.getMyPM(directReportGR.getValue("sys_id"))
				}
			);
		}
        return teamData.direct_reports;
	 },

	 // *****************************************************************************
     // Return A persons training actual totals for a FY
     // *****************************************************************************
     getMyTrnActuals: function(sysUserID,fy) {

		// GET Current FY if it was not passed in 
		if (!fy || fy=="") {
           var datetoconvert = new GlideDate();
           fycal = new global.AccountingCalendar(datetoconvert);
           fy = fycal.fy; //shows Fiscal Year
		}
        var actCount = new GlideRecord('x_saic4_saic_team_actuals');
        actCount.addQuery('employee', sysUserID);
        actCount.addQuery('fy', fy);
        actCount.query();   
        var totActuals  = 0;
        while (actCount.next()) {
           totActuals += actCount.getValue('total_burdened_cost')*1;
        }
		var impctrez  = parseFloat(Math.round(totActuals * 100) / 100);
        return impctrez;
	 },

	 // *****************************************************************************
     // Return A persons training actual detail records for a FY
     // *****************************************************************************
     getMyTrnActualsDetail: function(sysUserID,fy) {
//		 gs.info('sysUserID: '+sysUserID+' | fy: '+fy);

		// GET Current FY if it was not passed in 
		if (!fy || fy=="") {
           var datetoconvert = new GlideDate();
           fycal = new global.AccountingCalendar(datetoconvert);
           fy = fycal.fy; //shows Fiscal Year
		}

		var dd = new TeamMgmt();

		var actCount = new GlideRecord('x_saic4_saic_team_actuals');
        actCount.addQuery('employee', sysUserID);
        actCount.addQuery('fy', fy);
        actCount.orderByDesc('period');
        actCount.orderByDesc('sub_period');
        actCount.query();   
        var totActuals  = 0;
		var actRez = [];
        while (actCount.next()) {
			actRez.push({
                "account_id"              : actCount.getValue('account_id'),
                "charge_code"             : actCount.getValue('charge_code'),
                "project_nam"             : actCount.getValue('project_nam'),
                "period"                  : actCount.getValue('period'),
                "sub_period"              : actCount.getValue('sub_period'),
                "burden"                  : actCount.getValue('burden'),
                "transaction_amount"      : dd.formatDollar(actCount.getValue('transaction_amount')),
                "total_burdened_cost"     : dd.formatDollar(actCount.getValue('total_burdened_cost')),
				"transaction_description" : actCount.getValue('transaction_description')
			});
		}

		return actRez;
	 },

	 // *****************************************************************************
     // Return A persons training impact on the budget
     // *****************************************************************************
     getMyTrnBudgetImpact: function(impactedBudget, totApproved, totPending, totActuals, fy) {
        var pcntPendingImp  = 0;
        var pcntApprovedImp = 0;
        var pcntAcutalsImp  = 0;
        var budget          = 0;
        var budgetBalance   = 0;
        var pending_demand  = 0;
        var approved_demand = 0;
        var actuals_to_date = 0;

		var impctrez        = {};

		if (!impactedBudget) {
           impctrez.pcntPendingImp  = pcntPendingImp;
           impctrez.pcntApprovedImp = pcntPendingImp;
           impctrez.pcntAcutalsImp  = pcntAcutalsImp;
		   impctrez.budget          = 'not passed';
//		   gs.info('A) impactedBudget: NOT PASSED - this could be because the user has acutals but no requests');
           return impctrez;			
        } else {
//          gs.info('A) impactedBudget: '+impactedBudget.toString()+'  totApproved: '+totApproved+'  totPending: '+totPending+'  totActuals: '+totActuals);

          // GET Current FY if it was not passed in 
          if (!fy || fy=="") {
             var datetoconvert = new GlideDate();
             fycal = new global.AccountingCalendar(datetoconvert);
             fy = fycal.fy; //shows Fiscal Year
          }
          var trngrbdgt = new GlideRecord('x_saic4_saic_team_training_budget');
          trngrbdgt.addQuery('fy', fy);
          trngrbdgt.addEncodedQuery('sys_id='+impactedBudget);
          trngrbdgt.query();
          while (trngrbdgt.next()) {
	//		  parseFloat(Math.round(num3 * 100) / 100).toFixed(2)
			 pcntPendingImp  = (parseFloat(totPending)  / parseFloat(trngrbdgt.getValue('budget_total'))) * 100;
			 pcntApprovedImp = (parseFloat(totApproved) / parseFloat(trngrbdgt.getValue('budget_total'))) * 100;
			 pcntAcutalsImp  = (parseFloat(totActuals)  / parseFloat(trngrbdgt.getValue('budget_total'))) * 100;
             budget          = trngrbdgt.getValue('budget_total');  
             budgetBalance   = trngrbdgt.getValue('balance');  
             pending_demand  = trngrbdgt.getValue('pending_demand');  
             actuals_to_date = trngrbdgt.getValue('actuals_to_date');  
             approved_demand = trngrbdgt.getValue('approved_demand');  
		  }
		  impctrez.pcntPendingImp  = pcntPendingImp.toFixed(2);
		  impctrez.pcntApprovedImp = pcntApprovedImp.toFixed(2);
		  impctrez.pcntAcutalsImp  = pcntAcutalsImp.toFixed(2);
		  impctrez.budget          = parseFloat(budget);
		  impctrez.budgetBalance   = parseFloat(budgetBalance);
		  impctrez.budgetPending   = parseFloat(pending_demand);
		  impctrez.budgetActuals   = parseFloat(actuals_to_date);
		  impctrez.budgetApproved  = parseFloat(approved_demand);


//		  gs.info('B) impactedBudget: '+impactedBudget.toString()+' | pcntPendingImp: '+pcntPendingImp+' | pcntApprovedImp: '+pcntApprovedImp+' | pcntAcutalsImp: '+pcntAcutalsImp);
          return impctrez;
		}
	 },

	 // *****************************************************************************
     // Return A persons teams training activity
     // *****************************************************************************
     getMyTrnActivty: function(sysUserID,fy) {

		// GET Current FY if it was not passed in 
		if (!fy || fy=="") {
           var datetoconvert = new GlideDate();
           fycal = new global.AccountingCalendar(datetoconvert);
           fy = fycal.fy; //shows Fiscal Year
		}
        var totPending       = 0;
		var totApproved      = 0;
		var totDenied        = 0;
        var totPendingCount  = 0;
		var totApprovedCount = 0;
		var totDeniedCount   = 0;
		var total_cost       = 0;
		var impactedBudget   = "";
		var impactedBudgetNm = "";
		var requestedFor     = "";
		var impctrez         = {};

		var usrStats = new GlideRecord('x_saic4_saic_team_training_requests');
        usrStats.addQuery('approval_status','IN','Pending,Approved,Denied');
        usrStats.addQuery('fy',fy);
		usrStats.addEncodedQuery('requested_for.IN'+sysUserID);
        usrStats.query();
        while (usrStats.next()) {
			total_cost =  usrStats.getValue('estimated_course_cost')*1;
			total_cost += usrStats.getValue('estimated_labor_cost')*1;
			total_cost += usrStats.getValue('estimated_exam_fees')*1;
			total_cost += usrStats.getValue('estimated_lab_fees_books')*1;
			total_cost += usrStats.getValue('estimated_travel_cost')*1;
			if (usrStats.approval_status =='Pending') {
				totPending       += total_cost; //parseFloat(total_cost);
				totPendingCount  += 1;
			}
			if (usrStats.approval_status =='Approved') {
				totApproved      += total_cost; //parseFloat(total_cost);
				totApprovedCount += 1;
			}
			if (usrStats.approval_status =='Denied') {
				totDenied        += total_cost; //parseFloat(total_cost);
				totDeniedCount   += 1;
			}
			if (impactedBudget=='') {
		       impactedBudget   = usrStats.budget;
               impactedBudgetNm	= usrStats.getDisplayValue('budget');
               requestedFor     = usrStats.getDisplayValue('requested_for');
			}
        }
		impctrez.totPending       = totPending; //parseFloat(Math.round(totPending * 100) / 100);
		impctrez.totPendingCount  = totPendingCount;
		impctrez.totApproved      = totApproved; //parseFloat(Math.round(totApproved * 100) / 100);
		impctrez.totApprovedCount = totApprovedCount;
		impctrez.totDenied        = totDenied; //parseFloat(Math.round(totDenied * 100) / 100);
		impctrez.totDeniedCount   = totDeniedCount;
		impctrez.impactedBudget   = impactedBudget.toString();
		impctrez.impactedBudgetNm = impactedBudgetNm;

        return impctrez;
	 },

	 // *****************************************************************************
     // Return A persons teams training activity
     // *****************************************************************************
     getMyTeamTrainActivty: function(sysUserID) {
		var fn = new TeamMgmt();
        var teamData = {};
        teamData.direct_reports = [];

        var directReportGR = new GlideRecord("sys_user");
        directReportGR.addActiveQuery();
		var encQry = fn.getMyTeamSysIDs(sysUserID,sysUserID);
		directReportGR.addEncodedQuery(encQry);
        directReportGR.orderBy('last_name');
        directReportGR.query();
        while(directReportGR.next()) {
			var totActuals = fn.getMyTrnActuals(directReportGR.getValue("sys_id"));
			var empAct     = fn.getMyTrnActivty(directReportGR.getValue("sys_id"));
			var empImpact  = fn.getMyTrnBudgetImpact(empAct.impactedBudget, empAct.totApproved, empAct.totPending, totActuals);
            teamData.direct_reports.push(
				{
                  "email"                  : directReportGR.getValue("email") || "",
                  "first_name"             : directReportGR.getValue("first_name"),
                  "last_name"              : directReportGR.getValue("last_name"),
                  "name"                   : directReportGR.getValue("name"),
                  "phone"                  : directReportGR.getValue("phone") || "",
                  "sys_id"                 : directReportGR.getValue("sys_id"),
			      "pmInfo"                 : fn.getMyPM(directReportGR.getValue("sys_id")),
			      "totPending"             : (empAct.totPending.toFixed(2))*1,
			      "totPendingCount"        : empAct.totPendingCount,
			      "totApproved"            : (empAct.totApproved.toFixed(2))*1,
			      "totApprovedCount"       : empAct.totApprovedCount,
			      "totDenied"              : (empAct.totDenied.toFixed(2))*1,
			      "totDeniedCount"         : empAct.totDeniedCount,
                  "totActuals"             : (totActuals.toFixed(2))*1,
                  "pcntPendingImp"         : empImpact.pcntPendingImp,
                  "pcntApprovedImp"        : empImpact.pcntApprovedImp,
                  "pcntAcutalsImp"         : empImpact.pcntAcutalsImp,
                  "impactedBudgetAmt"      : empImpact.budget,
                  "impactedbudgetBalance"  : empImpact.budgetBalance,
                  "impactedbudgetPending"  : empImpact.budgetPending,
                  "impactedbudgetActuals"  : empImpact.budgetActuals,
                  "impactedbudgetApproved" : empImpact.budgetApproved,
                  "impactedBudgetNm"       : empAct.impactedBudgetNm
				}
			);
		}
        return teamData.direct_reports;
	 },

	 // *******************************************************************************************
     // Return Stats of a particular capability by users team in array format (for highcharts)
     // *******************************************************************************************
     getPieStats: function(sysUserID,area) {
        var tbl,col,title;
		if (area=='agencies') {
			tbl   = 'u_rms_agency';
			col   = 'u_agency';
			title = 'Angencies';
		} else if (area=='cert') {
			tbl   = 'u_rms_certifications';
			col   = 'u_certification';
			title = 'Certifications';
		} else if (area=='location') {
			tbl   = 'u_rms_location';
			col   = 'u_location';
			title = 'Locations';
		} else if (area=='comptech') {
			tbl   = 'u_rms_computer_technology';
			col   = 'u_computer_technology';
			title = 'Computer Technology';
		} else if (area=='lang') {
			tbl   = 'u_rms_foreign_language';
			col   = 'u_lanaguage';
			title = 'Foreign Language';
		} else if (area=='ed') {
			tbl   = 'u_rms_education';
			col   = 'u_education';
			title = 'Education';
		} else if (area=='progmgmt') {
			tbl   = 'u_rms_program_management';
			col   = 'u_postion';
			title = 'Program Management';
		} else if (area=='prog') {
			tbl   = 'u_rms_program_type';
			col   = 'u_program_type';
			title = 'Program Types';
		} else if (area=='eng') {
			tbl   = 'u_rms_system_engineering';
			col   = 'u_system_engineering';
			title = 'Cystem Engineering';
		} else if (area=='techrole') {
			tbl   = 'u_rms_technical_role';
			col   = 'u_technical_role';
			title = 'Technical Role';
		} else if (area=='spec') {
			tbl   = 'u_rms_technical_specialty';
			col   = 'u_technical_specialty';
			title = 'Technical Specialty';
		} else if (area=='tools') {
			tbl   = 'u_rms_tools_vendor_experience';
			col   = 'u_tool_vendor';
			title = 'Vendor/Tools';
		}

		var series = []; 
		var data = [];
		var counts = [];
		var fn = new TeamMgmt();
		var encQry = fn.getMyTeamSysIDs(sysUserID,sysUserID);
        var statGR = new GlideAggregate(tbl);
//gs.info(encQry);
		 statGR.addEncodedQuery('u_employee_name.'+encQry);
//        statGR.addAggregate('COUNT(DISTINCT',col);
        statGR.addAggregate('COUNT',col);
        statGR.groupBy(col);
        statGR.setGroup(false);
        statGR.query();
		 cnt = 0;
        while(statGR.next()) {
           data.push(statGR.getValue(col));
		   counts.push(1*statGR.getAggregate('COUNT',col));
			cnt += 1;
		}
		series.push( {
			"title"      : title,
			"categories" : data,
			"data"       : counts
		});
		return series; 
		//return details.substr(0,details.length-1);
	 },

	 // *****************************************************************************
     // Return Stats for for user passed in
     // *****************************************************************************
     getMyTeamStats: function(sysUserID) {
		var fn = new TeamMgmt();
		var encQry = fn.getMyTeamSysIDs(sysUserID,sysUserID);
        var teamStats = [];
        teamStats.push(
          {
            "memberCount"    : getUsersStat('sys_user',encQry,'memberCount') || 0,
            "location"       : getUsersStat('sys_user',encQry,'memberLocation') || 0,
			"jobFamily"      : getUsersStat('sys_user',encQry,'jobFamily') || 0,
            "agencies"       : getUsersStat('u_rms_agency','u_employee_name.'+encQry,'agency') || 0,
            "clearance"      : getUsersStat('u_rms_clearance','u_employee_name.'+encQry,'clear') || 0,
            "certifications" : getUsersStat('u_rms_certifications','u_employee_name.'+encQry,'cert') || 0,
            "computerTech"   : getUsersStat('u_rms_computer_technology','u_employee_name.'+encQry,'comptech') || 0,
            "language"       : getUsersStat('u_rms_foreign_language','u_employee_name.'+encQry,'lang') || 0,
			"education"      : getUsersStat('u_rms_education','u_employee_name.'+encQry,'ed') || 0,
			"progMgmt"       : getUsersStat('u_rms_program_management','u_employee_name.'+encQry,'prgmgmt') || 0,
			"programs"       : getUsersStat('x_saic4_saic_team_employees_to_programs','employee.'+encQry,'prog') || 0,
			"engineering"    : getUsersStat('u_rms_system_engineering','u_employee_name.'+encQry,'eng') || 0,
			"techRole"       : getUsersStat('u_rms_technical_role','u_employee_name.'+encQry,'techrole') || 0,
			"speciality"     : getUsersStat('u_rms_technical_specialty','u_employee_name.'+encQry,'spec') || 0,
			"toolsVendors"   : getUsersStat('u_rms_tools_vendor_experience','u_employee_name.'+encQry,'vend') || 0
          }
        );
        return teamStats;

        // sys_user stat function
		function getUsersStat(tbl,usrScope,qtype) {
           var teamStatsGR = new GlideAggregate(tbl);
           if (tbl=='sys_user') {
              teamStatsGR.addQuery('active',true);
		   }
           teamStatsGR.addEncodedQuery(usrScope);
           if (qtype == 'memberLocation') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','location');
              teamStatsGR.groupBy('location');
              teamStatsGR.setGroup(false);
		   } else if (qtype == 'memberCount') {
              teamStatsGR.addAggregate('COUNT');
           } else if (qtype == 'jobFamily') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','u_job_code');
              teamStatsGR.groupBy('u_job_code');
              teamStatsGR.setGroup(false);
		   } else if (qtype == 'agency') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','u_agency');
              teamStatsGR.groupBy('u_agency');
              teamStatsGR.setGroup(false);
		   } else if (qtype == 'cert') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','u_certification');
              teamStatsGR.groupBy('u_certification');
              teamStatsGR.setGroup(false);
		   } else if (qtype == 'clear') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','u_clearance_type');
              teamStatsGR.groupBy('u_clearance_type');
              teamStatsGR.setGroup(false);
		   } else if (qtype == 'comptech') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','u_computer_technology');
              teamStatsGR.groupBy('u_computer_technology');
              teamStatsGR.setGroup(false);
		   } else if (qtype == 'lang') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','u_lanaguage');
              teamStatsGR.groupBy('u_lanaguage');
              teamStatsGR.setGroup(false);
		   } else if (qtype == 'ed') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','u_education');
              teamStatsGR.groupBy('u_education');
              teamStatsGR.setGroup(false);
		   } else if (qtype == 'prgmgmt') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','u_postion');
              teamStatsGR.groupBy('u_postion');
              teamStatsGR.setGroup(false);
		   } else if (qtype == 'prog') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','program.customer');
              teamStatsGR.groupBy('program.customer');
              teamStatsGR.setGroup(false);
		   } else if (qtype == 'eng') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','u_system_engineering');
              teamStatsGR.groupBy('u_system_engineering');
              teamStatsGR.setGroup(false);
		   } else if (qtype == 'techrole') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','u_technical_role');
              teamStatsGR.groupBy('u_technical_role');
              teamStatsGR.setGroup(false);
		   } else if (qtype == 'spec') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','u_technical_specialty');
              teamStatsGR.groupBy('u_technical_specialty');
              teamStatsGR.setGroup(false);
		   } else if (qtype == 'vend') {
              teamStatsGR.addAggregate('COUNT(DISTINCT','u_tool_vendor');
              teamStatsGR.groupBy('u_tool_vendor');
              teamStatsGR.setGroup(false);
		   } 
           teamStatsGR.query();
           var rtnCount = 0;
           while(teamStatsGR.next()) {
              if (qtype == 'memberLocation') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','location')*1;
              } else if (qtype == 'memberCount') {
                rtnCount = teamStatsGR.getAggregate('COUNT');
              } else if (qtype == 'jobFamily') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','u_job_code')*1;
              } else if (qtype == 'agency') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','u_agency')*1;
              } else if (qtype == 'cert') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','u_certification')*1;
              } else if (qtype == 'clear') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','u_clearance_type')*1;
              } else if (qtype == 'comptech') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','u_computer_technology')*1;
              } else if (qtype == 'lang') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','u_lanaguage')*1;
              } else if (qtype == 'ed') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','u_education')*1;
              } else if (qtype == 'prgmgmt') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','u_postion')*1;
              } else if (qtype == 'prog') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','program.customer')*1;
              } else if (qtype == 'eng') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','u_system_engineering')*1;
              } else if (qtype == 'techrole') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','u_technical_role')*1;
              } else if (qtype == 'spec') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','u_technical_specialty')*1;
              } else if (qtype == 'vend') {
                rtnCount += teamStatsGR.getAggregate('COUNT(DISTINCT','u_tool_vendor')*1;
              }
           }
		   return rtnCount;
	    }	 
	 },

	 // *****************************************************************************
     // Approve or Reject a request
     // *****************************************************************************
     updateCatRequest: function(sysapprover,appState,comments) {
		 var itmGr = new GlideRecord('sysapproval_approver');
         //itmGr.addEncodedQuery('sys_id=4cb8042f135a0300aa7f5eff3244b0c4');
         itmGr.addEncodedQuery('sys_id='+sysapprover);
         itmGr.query();
		 var rtnValue = "";
		 if (itmGr.getRowCount() < 1) {
			 gs.info('SI:updateCatRequest - no record found for sysapprover:'+sysapprover);
			 return 'An error occured.  No action taken.';
		 } else {
           while (itmGr.next()) {
	          itmGr.state = appState;
			  itmGr.comments = comments;
	          rtnValue = itmGr.update();
           }
		   return 'Request has been '+appState;
		 }
	 },

	 // *****************************************************************************
     // Get Approval Status
     // *****************************************************************************
     getRequestApprovalStatus: function(reqSysID) {
		 var aprvrGr = new GlideRecord('sysapproval_approver');
         //itmGr.addEncodedQuery('sys_id=4cb8042f135a0300aa7f5eff3244b0c4');
         aprvrGr.addEncodedQuery('sysapproval='+reqSysID);
         aprvrGr.query();
		 var approvers = [];
         var details = [];
		 if (aprvrGr.getRowCount() < 1) {
            gs.info('SI:getRequestApprovalStatus - no record found for sysapproval:'+reqSysID);
            return 'An error occured.  No action taken.';
		 } else {
			var overallStatus = "";
            while (aprvrGr.next()) {
			  var recState = aprvrGr.getValue('state');
			  if (recState =='rejected') {
				  overallStatus = 'Rejected';
			  } else if (recState =='approved') {
				  overallStatus = 'Approved';
			  } else if (recState =='cancelled') {
				  overallStatus = 'Cancelled';
			  } else {
				  overallStatus = recState;
			  }

              // get all approvers and comments
              var comments = aprvrGr.comments.getJournalEntry(-1);
			  comments = comments.split("\n\n");
              for (var i = 0; i < comments.length; i++) {
				  comments[i] = comments[i].replace('(Comments)\n','');
			  }               

			  details.push(
			    {
                 "approverSysID"  : aprvrGr.approver.toString(),
                 "approver"       : aprvrGr.getDisplayValue('approver'),
                 "state"          : aprvrGr.getDisplayValue('state'),
                 "comments"       : comments
			    }
              );
            }
 			approvers.push({
				 "status" : overallStatus,
				 "details" : details
			});


//		   approvers.status = overallStatus;
		   return approvers;
		 }
	 },

	// *****************************************************************************
     // GET my PM if you can
	 // *****************************************************************************
     getMyPM: function(sysUserID) {
        var pm = new GlideRecord('x_saic4_saic_team_employees_to_programs');
        pm.addQuery('employee', sysUserID);
        pm.query();
		var PMs = {};
		PMs.details = [];
        if (pm.getRowCount() < 1) {
           return 'no pm';
        } else {
           while (pm.next()) {
			 PMs.details.push(
			   {
                "pmSysID"  : pm.program.program_manager.toString(),
                "pm_name"  : pm.getDisplayValue('program.program_manager'),
                "popStart" : pm.getDisplayValue('program.pop_start'),
                "popEnd"   : pm.getDisplayValue('program.pop_stop'),
                "contract" : pm.getDisplayValue('program.number'),
                "title"    : pm.getDisplayValue('program.title'),
                "customer" : pm.getDisplayValue('program.customer'),
			   }
             );
           }
        }
	    return PMs.details;
	 },

	 // *****************************************************************************
     // Return general total stats on actuals
     // *****************************************************************************
     getActualsStats: function() {
        // Calculate current FY
		var datetoconvert = new GlideDate();
        fycal = new global.AccountingCalendar(datetoconvert);
        var fy = fycal.fy; //shows Fiscal Year

        var series = [];

        var trngActuals = new GlideAggregate('x_saic4_saic_team_actuals');
		trngActuals.groupBy('project_name');
// 		trngActuals.setGroup(false);
        trngActuals.addQuery('fy', fy);
        trngActuals.query();
        while (trngActuals.next()) {
//			gs.info('getActualsStats: ' + trngActuals.getAggregate('COUNT'));
			var project_name = trngActuals.getValue('project_name');
            var pdActuals = new GlideAggregate('x_saic4_saic_team_actuals');
            pdActuals.groupBy('period');
            pdActuals.orderBy('period');
            pdActuals.addAggregate('SUM', 'total_burdened_cost');
            pdActuals.addQuery('fy', fy);
            pdActuals.addQuery('project_name', project_name);
            pdActuals.query();
            var pdData = [];
            var counter = 0;
            while (pdActuals.next()) {
				counter = counter + 1;
                for (var i=counter;i<13;i++) {
				    var pd    = pdActuals.getValue('period');
				    pd        = parseFloat(pd);
				    var bcost = pdActuals.getAggregate('SUM', 'total_burdened_cost');
				    bcost     = parseFloat(bcost);
					//bcost     = bcost.toFixed(2);
    			    if (i == pd) {
//                       pdData.push(pd);   this was added for debugging to ensure the value was for the right period
                       pdData.push(bcost);
				       break;
				    } else {
//                       pdData.push(i);    see above
                       pdData.push(0);
				    }
				}
			}
			series.push({
				 "name" : trngActuals.getValue('project_name'),
				 "data" : pdData
			});
		}
		return series;

     },
