sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	var usr      = source.u_empl_id.toString();
	var contract = source.u_crn.toString();
	var info = "";

	var pu = new TeamMgmt();

	// retrieve employee sys_id from sys_user table...
	var emp_sysid =  pu.userTranslateEmpIDtoSysID(usr);
	// retrieve contract sys_id from contract table...
	var contract_sysid =  pu.userTranslateCrnDtoSysID(contract);

	if (emp_sysid=='not found' || contract_sysid=='not found') {
      info = 'Employee Contract Import error: Employee # ' + usr + ' or Contract #: ' + contract + ' is not valid';		       log.warn( info ); 
      ignore = true;		
	} else {
	  ignore = false;
	}

})(source, map, log, target);
