sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	var pm = source.u_program_manager.toString();
	var no = source.u_number.toString();
	var info = "";
	var pu = new TeamMgmt();
    // lookup the employee to ensure it's in sys_user table...
	var emp_sysid =  pu.userTranslateEmpIDtoSysID(pm);
    if (emp_sysid!='not found') {
	   return  emp_sysid;
    } else {
      info = 'Import error: PM Employee # ' + pm + ' does not translate to an active user in the system.  Contract# ' + no;
      log.warn( info ); 
      ignore = true;
    }


})(source, map, log, target);
